mapper.update:
update Operator
set

    Account=#{Account}
    ,Password=#{Password}
    ,IsEnabled=#{IsEnabled}
    ,Salt=#{Salt}
 where RowId=#{RowId}   AND  TenantRowId=#{TenantRowId}

mapper.insert:
INSERT INTO Operator (
RowId, Account, Password, IsEnabled, CreateDate, Creater, Salt, TenantRowId, IsSystem
) VALUES( #{RowId}, #{Account}, #{Password}, #{IsEnabled}, #{CreateDate}, #{Creater}, #{Salt}, #{TenantRowId}, #{IsSystem} )
