mapper.select:
SELECT Role.* FROM Role inner join (select RowId from Role


        TenantRowId=#{TenantRowId}


        AND CreateDate>=#{map.CreateBeginDate}


        AND CreateDate<=#{map.CreateEndDate}



order by #{map.OrderByColumn} limit #{map.BeginRowIndex},#{map.EndRowCount}) as a on a.RowId=Role.RowId

select count(*) from Role


        TenantRowId=#{TenantRowId}


        CreateDate>=#{map.CreateBeginDate}


        AND CreateDate<=#{map.CreateEndDate}

mapper.update:
update Role
set

    Code=#{Code}
    ,Name=#{Name}
 ,DisplayNum=#{DisplayNum}
    ,Remark=#{Remark}
    ,Pinyin=#{Pinyin}
 where RowId=#{RowId}   AND   TenantRowId=#{TenantRowId}

mapper.insert:
INSERT INTO Role (
RowId, Code, Name, IsEnabled, CreateDate, Creater, DisplayNum, Remark, Pinyin, TenantRowId
) VALUES( #{RowId}, #{Code}, #{Name}, #{IsEnabled}, #{CreateDate}, #{Creater}, #{DisplayNum}, #{Remark}, #{Pinyin}, #{TenantRowId} )
