mapper.update:
update SystemConfig
set

    KeyName=#{KeyName}
    ,KeyCode=#{KeyCode}
    ,KeyValue=#{KeyValue}
    ,ConfigTypeName=#{ConfigTypeName}
    ,Remark=#{Remark}
    ,DisplayNum=#{DisplayNum}
 where RowId=#{RowId}   AND TenantRowId=#{TenantRowId};

mapper.insert:
INSERT INTO SystemConfig (
RowId, ParamId, KeyName, KeyCode, KeyValue, ConfigTypeName, Remark, DisplayNum, Creater, CreateDate, TenantRowId
) VALUES( #{RowId}, #{ParamId}, #{KeyName}, #{KeyCode}, #{KeyValue}, #{ConfigTypeName}, #{Remark}, #{DisplayNum}, #{Creater}, #{CreateDate}, #{TenantRowId} )
