mapper.update:
update SystemResource
set
Code=#{Code}
,Name=#{Name}
,TypeName=#{TypeName}
,PathName=#{PathName}
,ParentRowId=#{ParentRowId}
,ParentRowIds=#{ParentRowIds}
,Permission=#{Permission}
,IsEnabled=#{IsEnabled}
,Pinyin=#{Pinyin}
,FullCode=#{FullCode}
,DisplayNum=#{DisplayNum}
,DropdownType=#{DropdownType}
,TypeStatus=#{TypeStatus}
where RowId=#{RowId}

mapper.insert:
INSERT INTO SystemResource (
RowId, Code, Name, TypeName, PathName, ParentRowId, ParentRowIds, Permission, IsEnabled, Pinyin, FullCode, DisplayNum, DropdownType, IsCS, AssemblyPath, TypeStatus
) VALUES( #{RowId}, #{Code}, #{Name}, #{TypeName}, #{PathName}, #{ParentRowId}, #{ParentRowIds}, #{Permission}, #{IsEnabled}, #{Pinyin}, #{FullCode}, #{DisplayNum}, #{DropdownType}, #{IsCS}, #{AssemblyPath}, #{TypeStatus} )
