mapper.update:
update OperatorConfig
set

    CellCount=#{CellCount}
    ,TenantRowId=#{TenantRowId}
 where RowId=#{RowId} AND TenantRowId=#{TenantRowId}

mapper.insert:
INSERT INTO OperatorConfig (
RowId, CellCount, TenantRowId, CreateDate, Creater
) VALUES( #{RowId}, #{CellCount}, #{TenantRowId}, #{CreateDate}, #{Creater} )
